/**
 * This is an Ajax Loader Image on the right bottom of the screen
 */

var loader = function() {
  this.name = 'loader' + (new Date().getTime()) + Math.floor(Math.random()*100000);
  var me = this;
  $('body').append('<div id="'+me.name+'" style="'+this.getStyle()+'"></div>');
  jQuery.ajaxSetup({
    beforeSend: function() {
      me.on();
    },
    complete: function(){
      me.off();
    },
    success: function() {}
  });
};

loader.prototype.on = function() {
  $('#'+this.name).show();
};

loader.prototype.off = function() {
  $('#'+this.name).hide();
};



loader.prototype.getStyle = function() {
  return [
    'background-image:url('+this.getBackgroundImage()+')',
    'width:24px',
    'height:24px',
    'bottom:7px',
    'right:7px',
    'position:absolute'
  ].join(';');
};

/**
 * The Loader Image as base64 source
 */
loader.prototype.getBackgroundImage = function() {
  var loader_gif = '';
  return 'data:image/gif;base64,'+loader_gif;
};

